shared/schema.ts.content:
import { sql } from "drizzle-orm";
import { pgTable, text, varchar, timestamp, integer, boolean } from "drizzle-orm/pg-core";
import { createInsertSchema } from "drizzle-zod";
import { z } from "zod";

export const users = pgTable("users", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  username: text("username").notNull().unique(),
  password: text("password").notNull(),
});

export const contactSubmissions = pgTable("contact_submissions", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  name: text("name").notNull(),
  email: text("email").notNull(),
  phone: text("phone").notNull(),
  purpose: text("purpose").notNull(),
  message: text("message").notNull(),
  briefMessage: text("brief_message"),
  createdAt: timestamp("created_at").notNull().defaultNow(),
});

export const testimonials = pgTable("testimonials", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  name: text("name").notNull(),
  content: text("content").notNull(),
  rating: integer("rating").notNull(),
  createdAt: timestamp("created_at").notNull().defaultNow(),
});

export const blogs = pgTable("blogs", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  title: text("title").notNull(),
  excerpt: text("excerpt").notNull(),
  content: text("content").notNull(),
  author: text("author").notNull(),
  imageUrl: text("image_url"),
  createdAt: timestamp("created_at").notNull().defaultNow(),
});

export const packages = pgTable("packages", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  name: text("name").notNull(),
  description: text("description").notNull(),
  price: integer("price").notNull(),
  duration: text("duration").notNull(),
  features: text("features").array().notNull(),
  createdAt: timestamp("created_at").notNull().defaultNow(),
});

export const paymentTracking = pgTable("payment_tracking", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  razorpayOrderId: text("razorpay_order_id"),
  name: text("name").notNull(),
  email: text("email").notNull(),
  phone: text("phone").notNull(),
  packageId: varchar("package_id").notNull(),
  packageName: text("package_name").notNull(),
  status: text("status").notNull().default("pending"),
  createdAt: timestamp("created_at").notNull().defaultNow(),
});

export const razorpayOrders = pgTable("razorpay_orders", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  razorpayOrderId: text("razorpay_order_id").notNull().unique(),
  packageId: varchar("package_id").notNull(),
  packageName: text("package_name").notNull(),
  amount: integer("amount").notNull(),
  customerName: text("customer_name").notNull(),
  customerEmail: text("customer_email").notNull(),
  customerPhone: text("customer_phone").notNull(),
  status: text("status").notNull().default("created"),
  createdAt: timestamp("created_at").notNull().defaultNow(),
});

export const mentoriaPackages = pgTable("mentoria_packages", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  name: text("name").notNull(),
  description: text("description").notNull(),
  price: integer("price").notNull(),
  category: text("category").notNull(),
  features: text("features").array().notNull(),
  isPopular: boolean("is_popular").notNull().default(false),
  isActive: boolean("is_active").notNull().default(true),
  createdAt: timestamp("created_at").notNull().defaultNow(),
});

export const insertUserSchema = createInsertSchema(users).pick({
  username: true,
  password: true,
});

export const insertContactSubmissionSchema = createInsertSchema(contactSubmissions).omit({
  id: true,
  createdAt: true,
});

export const insertTestimonialSchema = createInsertSchema(testimonials).omit({
  id: true,
  createdAt: true,
});

export const insertBlogSchema = createInsertSchema(blogs).omit({
  id: true,
  createdAt: true,
});

export const insertPackageSchema = createInsertSchema(packages).omit({
  id: true,
  createdAt: true,
});

export const insertPaymentTrackingSchema = createInsertSchema(paymentTracking).omit({
  id: true,
  createdAt: true,
});

export const insertRazorpayOrderSchema = createInsertSchema(razorpayOrders).omit({
  id: true,
  createdAt: true,
});

export const insertMentoriaPackageSchema = createInsertSchema(mentoriaPackages).omit({
  id: true,
  createdAt: true,
});

export type InsertUser = z.infer<typeof insertUserSchema>;
export type User = typeof users.$inferSelect;
export type InsertContactSubmission = z.infer<typeof insertContactSubmissionSchema>;
export type ContactSubmission = typeof contactSubmissions.$inferSelect;
export type InsertTestimonial = z.infer<typeof insertTestimonialSchema>;
export type Testimonial = typeof testimonials.$inferSelect;
export type InsertBlog = z.infer<typeof insertBlogSchema>;
export type Blog = typeof blogs.$inferSelect;
export type InsertPackage = z.infer<typeof insertPackageSchema>;
export type Package = typeof packages.$inferSelect;
export type InsertPaymentTracking = z.infer<typeof insertPaymentTrackingSchema>;
export type PaymentTracking = typeof paymentTracking.$inferSelect;
export type InsertRazorpayOrder = z.infer<typeof insertRazorpayOrderSchema>;
export type RazorpayOrder = typeof razorpayOrders.$inferSelect;
export type InsertMentoriaPackage = z.infer<typeof insertMentoriaPackageSchema>;
export type MentoriaPackage = typeof mentoriaPackages.$inferSelect;

server/init.ts.content:
import { storage } from "./storage";
import bcrypt from "bcrypt";

export async function initializeAdmin() {
  try {
    const adminUsername = process.env.ADMIN_USERNAME || "admin";
    const adminPassword = process.env.ADMIN_PASSWORD;

    if (!adminPassword) {
      console.error("âš ï¸  SECURITY WARNING: ADMIN_PASSWORD environment variable is not set!");
      console.error("âš ï¸  Admin user initialization skipped. Please set ADMIN_USERNAME and ADMIN_PASSWORD environment variables.");
      console.error("âš ï¸  In production, never use default credentials!");
      return;
    }

    const existingAdmin = await storage.getUserByUsername(adminUsername);

    if (!existingAdmin) {
      const hashedPassword = await bcrypt.hash(adminPassword, 10);
      await storage.createUser({
        username: adminUsername,
        password: hashedPassword,
      });
      console.log(`âœ“ Admin user created with username: ${adminUsername}`);
      console.log("âš ï¸  Make sure to change your admin credentials after first login!");
    } else {
      console.log("âœ“ Admin user already exists");
    }
  } catch (error) {
    console.error("Error initializing admin user:", error);
  }
}

